mapper.select:
select
id, create_time, update_time, create_user, update_user, status, deleted, account, password, telephone, mark,
header_image
from jydboot.t_portal_user


        and id = #{id}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_user = #{createUser}


        and update_user = #{updateUser}


        and status = #{status}


        and deleted = #{deleted}


        and account = #{account}


        and password = #{password}


        and telephone = #{telephone}


        and mark = #{mark}


        and header_image = #{headerImage}
